import React, { useState } from "react";
import Login from "./components/Login";
import Register from "./components/Register";
import Dashboard from "./components/Dashboard";
import "./App.css";

function App() {
  const [view, setView] = useState("login"); // 'login' | 'register' | 'logged'
  const [fakeUser, setFakeUser] = useState(null);

  // Simula login (sin backend)
  const handleLogin = ({ usuario, contrasena }) => {
    console.log("LOGIN:", { usuario, contrasena });
    // Simular éxito
    setFakeUser({ usuario });
    setView("logged");
  };

  // Simula registro
  const handleRegister = (formData) => {
    console.log("REGISTER:", formData);
    alert("Registro simulado. Ahora inicia sesión.");
    setView("login");
  };

  const handleLogout = () => {
    setFakeUser(null);
    setView("login");
  };

  if (view === "register") {
    return (
      <Register
        onSubmit={handleRegister}
        onCancel={() => setView("login")}
      />
    );
  }

  if (view === "logged" && fakeUser) {
    return <Dashboard />;
  }

  // Vista Login por defecto
  return (
    <Login
      onSubmit={handleLogin}
      onShowRegister={() => setView("register")}
    />
  );
}

export default App;
