import React, { useState } from "react";
import Login from "./Login";
import Register from "./Register";
import Dashboard from "./Dashboard";
import "./App.css";

function App() {
  const [view, setView] = useState("login");
  const [fakeUser, setFakeUser] = useState(null);

  const handleLogin = (userData) => {
    setFakeUser(userData);
    setView("logged");
  };

  const handleRegister = (userData) => {
    setFakeUser(userData);
    setView("logged");
  };

  const handleLogout = () => {
    setFakeUser(null);
    setView("login");
  };

  if (view === "logged" && fakeUser) {
    return <Dashboard onLogout={handleLogout} />;
  }

  return (
    <div className="app-container">
      {view === "login" ? (
        <Login onSwitchToRegister={() => setView("register")} onLogin={handleLogin} />
      ) : (
        <Register onSwitchToLogin={() => setView("login")} onRegister={handleRegister} />
      )}
    </div>
  );
}

export default App;
